mapper.select:
SELECT *
FROM passage
WHERE published = 1
    LIMIT #{startPage}
    , #{endPage}

SELECT *
FROM passage
WHERE published = 0
  AND unreviewed = 0
    LIMIT #{startPage}
    , #{endPage}

SELECT *
FROM passage
WHERE unreviewed = 1 AND distributed = 0
    LIMIT #{startPage}
    , #{endPage}
